import React, { useEffect, useState } from "react";
import { Button, Form, Row, Dropdown } from "react-bootstrap";

// import { getLoginApi } from '../../services/api';

const InitialValues = {
  roleType: "",
  loginUserName: "",
  loginPassword: "",
};

export default function LogIn() {
  const [login, setLogin] = useState(InitialValues);
  const [formErrors, setFormErrors] = useState({});
  const [isSubmit, setIsSubmit] = useState(false);
  const [serverUser, setServerUser] = useState([]);

  const onValueChange2 = (e) => {
    console.log(e.target.name, e.target.innerText);
    setLogin({ ...login, [e.target.name]: e.target.innerText });
    console.log(login);
    if (e.target.innerText === "Victims") {
      console.log("Role of USer Victims");
    }
    if (e.target.innerText === "Organization") {
      console.log("Role of USer Organization");
    }
    if (e.target.innerText === "Volunteer") {
      console.log("Role of USer Volunteer");
    }
  };
  const onValueChange = (e) => {
    console.log(e.target.name, e.target.value);
    setLogin({ ...login, [e.target.name]: e.target.value });
    console.log(login);
  };

  useEffect(() => {
    console.log(formErrors);
    if (Object.keys(formErrors).length === 0 && loginUser) {
      console.log(login);
    }
  }, [formErrors]);

  const validate = (values) => {
    const errors = {};

    if (!values.loginUserName) {
      errors.loginUserName = "Username is required!";
    }
    if (!values.roleType) {
      errors.roleType = "Type Of User is Required!";
    }
    if (!values.loginPassword) {
      errors.loginPassword = "Password is required";
    } else if (values.loginPassword.length < 4) {
      errors.loginPassword = "Password must be more than 4 characters";
    } else if (values.loginPassword.length > 10) {
      errors.loginPassword = "Password cannot exceed more than 10 characters";
    }
    return errors;
  };
  const loginUser = (e) => {
    // await getLoginApi(login);\
    console.log("test");
    e.preventDefault();
    setFormErrors(validate(login));
    setIsSubmit(true);
    if (login.roleType === "Victims") {
      console.log("Vitims ROle selected");
    }
  };

  const getVictimsDetails = async (e) => {
    // let response = await getLoginApi(e);
    // console.log(response.data);
    // setServerUser(response.data);
  };

  return (
    <div className="d-flex justify-content-center">
      <div className="pt-5 pb-5 ">
        <Card style={styles.cardstyle}>
          <Alert variant="secondary">
            <Alert.Heading className="d-flex justify-content-center">
              Log In Page
            </Alert.Heading>
          </Alert>
        </Card>
        <form>
          {Object.keys(formErrors).length === 0 && isSubmit ? (
            <div className="ui message success">Signed in successfully</div>
          ) : (
            <pre>{JSON.stringify(login, undefined, 2)}</pre>
          )}
          <div className="Auth-form-content">
            <h3 className="Auth-form-title">Log-In Page</h3>
            <div className="form-group mt-3">
              <Form>
                <Form.Group
                  as={Row}
                  className="mb-3"
                  controlId="formBasicEmail"
                >
                  <Form.Label column sm={2}>
                    User Type
                  </Form.Label>

                  <Dropdown column sm={2}>
                    <Dropdown.Toggle variant="success" id="dropdown-basic">
                      Select type of user
                    </Dropdown.Toggle>

                    <Dropdown.Menu>
                      <Dropdown.Item
                        id="victims"
                        onClick={(e) => {
                          onValueChange2(e);
                        }}
                        name="roleType"
                      >
                        Victims
                      </Dropdown.Item>
                      <Dropdown.Item
                        id="organization"
                        onClick={(e) => {
                          onValueChange2(e);
                        }}
                        name="roleType"
                      >
                        Organization
                      </Dropdown.Item>
                      <Dropdown.Item
                        id="volunteer"
                        onClick={(e) => {
                          onValueChange2(e);
                        }}
                        name="roleType"
                      >
                        Volunteer
                      </Dropdown.Item>
                    </Dropdown.Menu>
                  </Dropdown>
                  <p>{formErrors.roleType}</p>
                </Form.Group>
              </Form>
              <Form.Group as={Row} className="mb-3" controlId="formBasicEmail">
                <Form.Label column sm={2}>
                  User Name
                </Form.Label>

                <Form.Control
                  type="text"
                  placeholder="Enter User Name"
                  style={{ width: "25%" }}
                  onChange={(e) => onValueChange(e)}
                  name="loginUserName"
                />
                <p>{formErrors.loginUserName}</p>
              </Form.Group>
              <Form.Group as={Row} className="mb-3" controlId="formBasicEmail">
                <Form.Label column sm={2}>
                  Password
                </Form.Label>

                <Form.Control
                  type="password"
                  placeholder="enter password"
                  style={{ width: "25%" }}
                  onChange={(e) => onValueChange(e)}
                  name="loginPassword"
                />
                <p>{formErrors.loginPassword}</p>
              </Form.Group>
              <Button
                onClick={(e) => loginUser(e)}
                variant="outline-success"
                type="login"
              >
                Log-In...
              </Button>
            </div>
          </div>
        </form>
      </div>
    </div>
  );
}
