import Button from "react-bootstrap/Button";

import Card from "react-bootstrap/Card";
import Form from "react-bootstrap/Form";
import Alert from "react-bootstrap/Alert";
import { Stack } from "react-bootstrap";
import { getData } from "../../api/api";
import { useState, useEffect } from "react";

function RequestSlip() {
  const [victimData, setVictims] = useState({});
  console.log("testing");
  useEffect(() => {
    getVictimData();
  }, []);

  const getVictimData = async () => {
    let res = await getData("1");
    console.log(res);
    setVictims(res.data);
  };

  // let victimData = [];
  // victimData = getData("cKS8-aY");
  console.log(victimData);
  const styles={
    cardstyle:{
      width: "30rem",

    }

  }
  return (
    <>
      <div className="d-flex justify-content-center">
        <div className="pt-5 pb-5">
          <Card style={styles.cardstyle} >
            <Alert variant="secondary">
              <Alert.Heading className="d-flex justify-content-center">
                RESCUE REQUEST
              </Alert.Heading>
              <div className="d-flex justify-content-end pt-3">
                Victim Id:1212
              </div>
            </Alert>
            <Form className="mt-1 p-3">
              <b>Dear **** **** (4545456)</b>
              <p>
                Thank You ! for using our online portal. Your request details
                are indicated below.
              </p>
              <br></br>
              <Stack direction="horizontal" gap={5}>
                <div>
                  <b>Request ID :</b> ***
                </div>
                <div>
                  <b> Request Date :</b> ******
                </div>
              </Stack>
              <div>
                <b> Name :</b> **** ****
              </div>
              <div>
                <b> Address :</b>
                {victimData.Address}
              </div>
              <div>
                <b> Contact No. :</b> {victimData.ContactNo}
              </div>
              <div>
                <b> Total Members in Group :</b> {victimData.CountPeople}
              </div>
              <div className="d-flex justify-content-center mt-5">
                <Button variant="success" type="submit">
                  Print
                </Button>
              </div>
            </Form>
          </Card>

        </div>

      </div>
    </>
  );
}

export default RequestSlip;
