import React, { useState } from "react";
import { useNavigate } from "react-router-dom";
import Card from "react-bootstrap/Card";
import Form from "react-bootstrap/Form";
import Row from "react-bootstrap/Row";
import { Button } from "react-bootstrap";
import Col from "react-bootstrap/Col";
import Alert from "react-bootstrap/Alert";

function Moneyhome() {
  const navigate = useNavigate();
  const [selected, setSelected] = useState("yes");
  const handleChange = (event) => {
    console.log(event.target.value);
    setSelected(event.target.value);
  };
  const styles = {
    cardstyle: {
      width: "29rem",
      marginBottom: "20rem",
    },
  };

  return (
    <div className="d-flex justify-content-center">
      <div className="pt-5 pb-5">
        <Card style={styles.cardstyle}>
          <Alert variant="secondary">
            <div className="d-flex justify-content-end mb-4">
              VICTIM ID : 1212
            </div>
            <Alert.Heading className="d-flex justify-content-center">
              DONATION PORTAL
            </Alert.Heading>
          </Alert>

          <Card.Body>
            <form id="myform">
              <table align="center">
                <h5>Enter amount in rupees</h5>

                <input name="amount" placeholder="Amount" required />

                <br></br>
                <br></br>

                <h5>Select payment mode</h5>

                <input
                  type="radio"
                  id="upi"
                  name="paymentmode"
                  value="upi"
                  checked={selected === "upi"}
                  onChange={handleChange}
                />
                <lable value="upi">UPI</lable>
                <br></br>

                <input
                  type="radio"
                  id="card"
                  name="paymentmode"
                  value="card"
                  checked={selected === "card"}
                  onChange={handleChange}
                />
                <lable value="card">Credit Card/Debit Card</lable>
                <br></br>

                <input
                  type="radio"
                  id="qr"
                  name="paymentmode"
                  value="qr"
                  checked={selected === "qr"}
                  onChange={handleChange}
                />
                <lable value="qr">Scane QR code</lable>
                <br></br>
                <br></br>

                <Row>
                  <Col>
                    <Button
                      className="d-flex justify-content-left"
                      onClick={() => {
                        if (selected === "upi") {
                          navigate("/upi");
                        } else if (selected === "card") {
                          navigate("/card");
                        } else if (selected === "qr") {
                          navigate("/qr");
                        } else {
                          alert("Please select Payment method");
                        }
                      }}
                    >
                      Next
                    </Button>
                  </Col>
                  <Col>
                    <Button
                      className="d-flex justify-content-right"
                      onClick={() => {
                        navigate("/donationhome");
                      }}
                      variant="success"
                    >
                      Back
                    </Button>
                  </Col>
                </Row>
              </table>
            </form>
          </Card.Body>
        </Card>
      </div>
    </div>
  );
}

export default Moneyhome;
